export const commonStyles = {
  button: {
    backgroundColor: '#2069e0',
    borderRadius: 15,
    width: "50%",
    alignItems: "center"
  },
  buttonText: {
    fontWeight: '400',
    fontSize: 20, 
    margin: 10,
    color: 'white',
  },
  title: {
    fontWeight: "bold",
    fontSize: 30, 
    textAlign: 'center',
  },
  content: {
    fontWeight: '400',
    fontSize: 24,
  },
}

export const lightStyles = {
  container: {
    flex: 1,
    backgroundColor: "#ffc",
  },
  text: {
    color: "black",
  },
  header: {
    backgroundColor: "yellow",
    height: 100,
    shadowColor: "black",
    shadowOpacity: 0.2,
    shadowRadius: 5,
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#f55"
  },
  headerTint: "#f55"
};

export const darkStyles = {
  container: {
    flex: 1,
    backgroundColor: "#878683",
  },
  text: {
    color: "white",
  },
  header: {
    backgroundColor: "#444444",
    height: 100,
    shadowColor: "black",
    shadowOpacity: 0.2,
    shadowRadius: 5,
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#f4d47c"
  },
  headerTint: "#f4d47c"
}
